nta.declaration:
// Place global declarations here.
const int CONVOYS = 1;
const int ZEROS[CONVOYS] = {0};
//const int CHOICE = 2;
const int STATIONS = 3;
const int TASKS = 2;
const int TASKS_ZERO[TASKS] = {0,0};
const int MAX_PROCESSORS = 10;
typedef int[0, CONVOYS - 1] id_t;

broadcast chan start_data_transfer;
broadcast chan move;

typedef struct {
	int processors; // Number of task the convoy is allowed to perform in parallel
	int send_to_station[STATIONS]; // Number of stations the convoy is allowed to send to in parallel
	int send_to_convoy[CONVOYS]; // Number of convoys this convoy is allowed to send to in parallel 
	int gather_data;
	int storage;
} ConvoyDescription;

typedef struct {
	int task_type;
	int execution_time;
	int data_rate;
} TaskDescription;

typedef struct {
	int cost;
	int data_recieved;
} StationDescription;

typedef struct {
	int task_type;
	int execution_time;
	int to_station;
} ExecutionDescription;

//const ConvoyDescription c2 = {15,{0,3},{0}};
const ConvoyDescription convoys[CONVOYS] = {{9,{3,3,0},{0}, 10, 1000}};
const TaskDescription t1 = {0,15, 2}; // Gather data
const TaskDescription t2 = {1,10, 2}; // Send to Station
const TaskDescription tasks[TASKS] = {t1,t2};

StationDescription stations[STATIONS] = {{2,0},{5,0},{10,0}};
int storages[CONVOYS] = {0};
int to_be_stored[CONVOYS] = {0};
ExecutionDescription convoy_tasks[CONVOYS][MAX_PROCESSORS] = {{{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,9999,-1},{-1,-1,-1}}};

int current_task = 0;

int proc_in_use[CONVOYS] = ZEROS;
int gather_in_use[CONVOYS] = ZEROS;
int send_to_station[CONVOYS][STATIONS] = {{0,0,0}};
int send_to_convoy[CONVOYS] = ZEROS;

clock TotalTime;
int schedule_length = 810;
int step = 0;
bool illegal_move = false;
bool execution_being_negative = false;
bool storage_overflow = false;
bool storage_underflow = false;
bool no_idle = false;

bool high_storage(ConvoyDescription& convoy, int id){
	return convoy.storage * 75/100 > storages[id];
}

bool medium_storage(ConvoyDescription& convoy, int id){
	return convoy.storage * 50/100 > storages[id];
}

bool low_storage(ConvoyDescription& convoy, int id){
	return convoy.storage * 25/100 > storages[id];
}

int accumulated_cost = 0;
int current_cost = 0;
int calculated_cost = 0;

void sort_lists(){
	int i,c,j;
	ExecutionDescription swap = {0,0,0};
	for (c = 0 ; c < CONVOYS ; c++){
		for (i = 0 ; i < convoys[c].processors - 1 ; i++){
			for (j = 0 ; j < convoys[c].processors - i - 1 ; j++){
				if (convoy_tasks[c][j].execution_time >= convoy_tasks[c][j+1].execution_time){
					swap = convoy_tasks[c][j];
					convoy_tasks[c][j] = convoy_tasks[c][j+1];
					convoy_tasks[c][j+1] = swap;
				}
			}	
		}
	}
}

template.declaration:
clock time;
int lowest;
void closest_task_to_finish(){	
	int i;
	lowest = 9999;	
	for (i = 0; i < CONVOYS; i++){
		if (convoy_tasks[i][0].execution_time < lowest){
			lowest = convoy_tasks[i][0].execution_time;
		}
	}
	if (lowest == 9999){
		no_idle = true;
		lowest = 1;
	}
}

void update_processors(){
	int i, c;
	for (c = 0; c < CONVOYS; c++){
		for (i = 0; i < convoys[c].processors; i++){
			if (convoy_tasks[c][i].task_type != -1){
				convoy_tasks[c][i].execution_time -= lowest;
			}
			if (convoy_tasks[c][i].execution_time == 0){
				if (convoy_tasks[c][i].to_station != -1){
					send_to_station[c][convoy_tasks[c][i].to_station]--;
				}
				if (convoy_tasks[c][i].task_type == 0){
					gather_in_use[c]--;
				}
				proc_in_use[c]--;
				convoy_tasks[c][i].to_station = -1;
				convoy_tasks[c][i].task_type = -1;
				convoy_tasks[c][i].execution_time = 9999;
			}
			else if (convoy_tasks[c][i].execution_time-lowest < 0){
				execution_being_negative = true;
			}
		}
	}
}



void update_storage(){
	int i, c;
	for (c = 0; c < CONVOYS; c++){
		to_be_stored[c] = 0;
		for (i = 0; i < MAX_PROCESSORS; i++){
			if (convoy_tasks[c][i].task_type == 0){ //Gather data
				to_be_stored[c] += tasks[convoy_tasks[c][i].task_type].data_rate * lowest;
				storages[c] += tasks[convoy_tasks[c][i].task_type].data_rate * lowest;
				if (storages[c] > convoys[c].storage){
					storage_overflow = true;
				}
			}
			else if (convoy_tasks[c][i].task_type == 1){ //Send data to Earth
				storages[c] -= tasks[convoy_tasks[c][i].task_type].data_rate * lowest;
				stations[convoy_tasks[c][i].to_station].data_recieved += tasks[convoy_tasks[c][i].task_type].data_rate * lowest;
				if (storages[c] < 0){
					storage_underflow = true;
				}
			}
		}
	}
}

int find_highest_cost(int c){
	int s;
	int highest = 0;
	for (s = 0; s < STATIONS; s++){
		if(convoys[c].send_to_station[s] != 0 && stations[s].cost > highest){
			highest = stations[s].cost;
		}
	}
	return highest;
}

void calc_cost(){
	int i, c, s, p;
	calculated_cost = 0;
	for (c = 0; c < CONVOYS; c++){
		//Cost goes up if we have low storage and no processes gathering data.
		if ((100-storages[c]*100/convoys[c].storage)*(100-gather_in_use[c]*100/convoys[c].processors)/1000 >=
		    (storages[c]*100/convoys[c].storage)*(gather_in_use[c]*100/convoys[c].processors)/1000){
			calculated_cost += (100-storages[c]*100/convoys[c].storage)*(100-gather_in_use[c]*100/convoys[c].processors)/1000;
		}
		else {
			calculated_cost += (storages[c]*100/convoys[c].storage)*(gather_in_use[c]*100/convoys[c].processors)/1000;
		}
		//Add cost of each processor sending to a station, in respect to the individial station's cost
		for (p = 0; p < convoys[c].processors; p++){
			if (convoy_tasks[c][p].task_type == 1){
				calculated_cost += stations[convoy_tasks[c][p].to_station].cost;
			}
		}
		//Cost goes up if we have high storage and no procceses sending to other convoys or stations on Earth
		for (s = 0; s < STATIONS ; s++){
			int internal_cost = find_highest_cost(c);
			if (send_to_station[c][s] == 0){
				if (((100-storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost))/1000 <
				    ((storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost))/1000){
					calculated_cost += ((100-storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost))/1000;
				}
				else{
					calculated_cost += ((storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost))/1000;
				}
			}
			else{
				if (((100-storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost)+(stations[s].cost*10/internal_cost)*(100-send_to_station[c][s]*100/convoys[c].send_to_station[s]))/1000 <
				    ((storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost)+(stations[s].cost*10/internal_cost)*(send_to_station[c][s]*100/convoys[c].send_to_station[s]))/1000){
					calculated_cost += ((100-storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost)+(stations[s].cost*10/internal_cost)*(100-send_to_station[c][s]*100/convoys[c].send_to_station[s]))/1000;
				}
				else{
					calculated_cost += ((storages[c]*100/convoys[c].storage)*(stations[s].cost*10/internal_cost)+(stations[s].cost*10/internal_cost)*(send_to_station[c][s]*100/convoys[c].send_to_station[s]))/1000;
				}
			}
		}
	}
}

bool check_constrains(){
	if (illegal_move){
		return false;
	}
	else if (execution_being_negative){
		return false;
	}
	else if (storage_overflow){
		return false;
	}
	else if (storage_underflow){
		return false;
	}
	else if (no_idle){
		return false;
	}
	return true;
}

int amount_of_tasks_to_execute(){
	if (current_task == 0){
		if (low_storage(convoys[id], id)){
			return convoys[id].processors;
		}
		else if (medium_storage(convoys[id], id)){
			return convoys[id].processors/2;
		}
		else if (high_storage(convoys[id], id)){
			return convoys[id].processors/3;
		}
	}
	else if (current_task == 1){
		if (low_storage(convoys[id], id)){
			return convoys[id].processors/3;
		}
		else if (medium_storage(convoys[id], id)){
			return convoys[id].processors/2;
		}
		else if (high_storage(convoys[id], id)){
			return convoys[id].processors;
		}
	}
	else{
		return 1;
	}
}

int cheapest_available_station(){
	int i;
	int lowest = 9999;
	int position = 2;
	for (i = 0; i < STATIONS; i++){
		if (convoys[id].send_to_station[i] > send_to_station[id][i]){
			if (stations[i].cost < lowest){
				lowest = stations[i].cost;
				position = i;
			}
		}
	}
	if (lowest == 9999){
		position = 9999;
	}
	return position;	
}

void insert_task(TaskDescription item){
	int i, station_index;
	for(i = 0; i < convoys[id].processors; i++){
		if (convoy_tasks[id][i].task_type == -1){
			proc_in_use[id]++;
			if (item.task_type == 0){// Gather data
				convoy_tasks[id][i].task_type = item.task_type;
				convoy_tasks[id][i].execution_time = item.execution_time;
				convoy_tasks[id][i].to_station = -1;
				gather_in_use[id]++;
				return;
			}
			else if (item.task_type == 1){//Send to station
				station_index = cheapest_available_station();
				if (station_index == 9999){
					return;
				}
				else{
					convoy_tasks[id][i].task_type = item.task_type;
					convoy_tasks[id][i].execution_time = item.execution_time;
					convoy_tasks[id][i].to_station = station_index;
					send_to_station[id][convoy_tasks[id][i].to_station]++;
					return;
				}
			}			
		}
	}

}

void add_tasks(TaskDescription task){
	int i = amount_of_tasks_to_execute();
	while(i){
		insert_task(task);
		i--;
	}	
}
