/** Task Description */
typedef struct {
	bool depend_win[W];
	int execution_time;
	int deadline;
	int prio;
	int data_rate;
	bool has_cost;
	int orientation;
	int res;
} TaskDescription;

/** Satellite Description */
typedef struct {
	int offset;
	int rotation;
	int memory;
	bool can_does[N];
	int suggested_task;
} SatDescription;

/** Window Description */
typedef struct {
	int start_time;
	int end_time;
	int price_rate;
} WindowDescription;

