void overStation(){
    if (OVER_STATION[index[RQ][window_to_check]][window_to_check][2]) {
        runnable[task_to_check] = true;
        win_active[window_to_check] = true;
    }
}

void next() {
    if (task_to_check < N) {
        if (window_to_check < STATIONS-1) {
            window_to_check++;
        }
        else {
            task_to_check ++;
            window_to_check = 0;     
        }
    }
    else {
        all_checked = true;
        task_to_check = 0;
        window_to_check = 0;
    }
}


void resetRunnable() {
	int i, s;
	for (i = 0; i < N; i++) {
		runnable[i] = false;
	}
    for (i = 0; i < STATIONS; i++){win_active[i] = false;}
}


bool independent() {
	int i = 0, count = 0;

	for (i = 0; i < STATIONS; i++){
		if (!jobs[task_to_check].depend_station[i]) {
			count ++;
		}
		if (count == STATIONS) {
			return true;
		}
	}
	return false;
}

void allTaskChecked(){
	if (task_to_check +1 == N){
		all_checked = true;
		task_to_check = 0;
		window_to_check = 0;
	}
    else task_to_check += 1;
}

void skipWindowCheck() {
	runnable[task_to_check] = true;

}

int windowEnd() {
	return OVER_STATION[index[RQ][window_to_check]][window_to_check][1];
}

int windowStart() {
    return OVER_STATION[index[RQ][window_to_check]][window_to_check][0];
}

bool depends() {
	return jobs[task_to_check].depend_station[window_to_check];
}

