void insert_task(){
    int i;
    for (i = 0; i < N; i++){
        if(waiting_queue[i] == -1){
            waiting_queue[i] = task;
            return; 
        }
    }
}

void dequeue(){
    int i;
    for (i = 0; i < N; i++){
        if (i+1 < N){
            waiting_queue[i] = waiting_queue[i+1];
        }
        if (waiting_queue[i] == -1){
            return;
        }
    }
}

void preemt_task() {
	boostPriority();
	updateProgress(jobs[task].execution_time/2);
    insert_task();
	task = active[sat_id];
	run_time[task] = 0;
}

void resume_task(){
    task = waiting_queue[0];
    dequeue();
}

clock loc_time, slew_time;
bool is_queued[N] = {0,0,0,0};
int selected = -1;

/** Change satellite orientation */
void turn_satellite(){
	if (sats[sat_id].rotation < jobs[active[sat_id]].orientation){sats[sat_id].rotation ++;}
	else if (sats[sat_id].rotation > jobs[active[sat_id]].orientation){sats[sat_id].rotation --;}
}

/** Suggest a new task to execute */
void suggest_task(){
	int i, j, k, count = 0, high = -1; bool cans[N] = {0,0,0,0};
	selected = -1;

	for (i = 0; i < N; i++){
		if (sats[sat_id].can_does[i]) { 
			cans[i] = true; }}

	for (i = 0; i < N - 1; i++) {
		if (cans[i]) {
			if (sats[sat_id].memory <= LOW_MEMORY ) {
				if (jobs[i].data_rate > 0) {
					if (priorities[sat_id][i]*2 <= 100) {
						priorities[sat_id][i] = priorities[sat_id][i] * 2; 
					}
					else {
						priorities[sat_id][i] = 100;
					}
				}
				else if (jobs[i].data_rate < 0) {
					priorities[sat_id][i] = 0;
				}
			}
			else if (sats[sat_id].memory >= HIGH_MEMORY) {
				if (jobs[i].data_rate < 0) {
					if (priorities[sat_id][i]*2 <= 100) {
						priorities[sat_id][i] = priorities[sat_id][i] * 2; 
					}
					else {
						priorities[sat_id][i] = 100;
					}
				}
				else if (jobs[i].data_rate > 0) {
					priorities[sat_id][i] = 0;
				} 
			}
		}
	}

	//TODO make logic for handeling ties
	for (i = 0; i < N - 1; i++){
		if (cans[i] && priorities[sat_id][i] > high){
			if(i != RECEIVE_INDEX ) {  // it is invalid to suggest a reeive task (It's the transferee that takes initiatve)
				selected = i;
				high = priorities[sat_id][i];
			}
		}
	}
	if (selected != -1 && high > 0) {
		is_queued[selected] = true;
		sats[sat_id].suggested_task = selected;
	}
	else {
	sats[sat_id].suggested_task = -1;
	}
}

bool all_checked = false;
int window_to_check = 0, task_to_check = 0;
