void overStation(){
    if (OVER_STATION[index[window_to_check]][window_to_check][2])
    {
        can_run = true;
    }else{
        can_run = false;
    }
}

void next() {
    if (task_to_check < N) {
        if (window_to_check < W-1) {
            window_to_check++;
        }
        else {
            task_to_check ++;
            window_to_check = 0;     
        }
    }
    else {
        all_checked = true;
        task_to_check = 0;
        window_to_check = 0;
    }
}


void resetRunnable() {
	int i;
	for (i = 0; i < N; i++) {
		runnable[i] = 0;
	}
}


/** text */ 
bool independent(){ 
	int i = 0, count = 0;

	for (i = 0; i < W; i++){
		if (!jobs[task_to_check].depend_win[i]) {
			count ++;
		}
		if (count == W) {
			return true;
		}
	}
	return false;
}

void allTaskChecked(){
	if (task_to_check +1 == N){
		all_checked = true;
		task_to_check = 0;
		window_to_check = 0;
	}
}

void skipWindowCheck() {
	runnable[task_to_check] = true;
	task_to_check += 1;
}

int windowEnd() {
	return OVER_STATION[index[window_to_check]][window_to_check][1];
}

bool depends() {
	return jobs[task_to_check].depend_win[window_to_check];
}

